/*Table structure for table `cases` */

DROP TABLE IF EXISTS `cases`;

CREATE TABLE `cases` (
  `case_id` int(11) NOT NULL AUTO_INCREMENT,
  `designer_id` int(11) DEFAULT NULL,
  `case_title` varchar(100) DEFAULT NULL COMMENT '案例标题',
  `case_desc` varchar(2000) DEFAULT NULL COMMENT '案例描述',
  `case_style` varchar(100) DEFAULT NULL COMMENT '设计风格',
  `case_money` varchar(100) DEFAULT NULL COMMENT '装修费用',
  `case_community` varchar(100) DEFAULT NULL COMMENT '所在楼盘小区',
  `case_acreage` varchar(100) DEFAULT NULL COMMENT '房屋面积',
  `case_area` varchar(100) DEFAULT NULL COMMENT '行政区域 朝阳区',
  `case_house_type` varchar(100) DEFAULT NULL COMMENT '装修户型',
  `case_designer` varchar(100) DEFAULT NULL COMMENT '设计师',
  `case_thumb_img` varchar(200) DEFAULT NULL COMMENT '缩略图',
  `case_read_num` int(11) DEFAULT NULL COMMENT '浏览量',
  `seo_title` varchar(500) DEFAULT NULL COMMENT 'SEO标题',
  `seo_keyword` varchar(500) DEFAULT NULL COMMENT 'SEO关键字',
  `seo_desc` varchar(500) DEFAULT NULL COMMENT 'SEO描述',
  `case_status` int(1) DEFAULT '1' COMMENT '1正常 3删除',
  `branch_company` varchar(100) DEFAULT NULL COMMENT '隶属分公司',
  `creater` varchar(10) DEFAULT NULL,
  `create_time` datetime DEFAULT NULL,
  `updater` varchar(10) DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  PRIMARY KEY (`case_id`)
) ENGINE=InnoDB AUTO_INCREMENT=68 DEFAULT CHARSET=utf8;

