/*Table structure for table `employee` */

DROP TABLE IF EXISTS `employee`;

CREATE TABLE `employee` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(100) DEFAULT NULL COMMENT '登录ID',
  `role_id` int(11) DEFAULT NULL COMMENT '角色ID',
  `nick_name` varchar(100) DEFAULT NULL COMMENT '显示名称',
  `avatar_photo` varchar(100) DEFAULT NULL COMMENT '头像路径',
  `password` varchar(100) DEFAULT NULL COMMENT '密码 md5',
  `age` int(3) DEFAULT NULL COMMENT '年龄',
  `sex` varchar(1) DEFAULT NULL COMMENT '性别',
  `id_number` varchar(100) DEFAULT NULL COMMENT '身份证号',
  `birthplace` varchar(100) DEFAULT NULL COMMENT '籍贯',
  `birthday` date DEFAULT NULL COMMENT '生日',
  `dept` varchar(100) DEFAULT NULL COMMENT '部门名称',
  `job_position` varchar(100) DEFAULT NULL COMMENT '岗位名称',
  `degree` varchar(100) DEFAULT NULL COMMENT '学历',
  `school_name` varchar(100) DEFAULT NULL COMMENT '毕业院校',
  `school_major` varchar(100) DEFAULT NULL COMMENT '主修专业',
  `hiredate` date DEFAULT NULL COMMENT '入职时间',
  `firedate` date DEFAULT NULL COMMENT '离职时间',
  `create_time` datetime DEFAULT NULL,
  `creater` varchar(100) DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  `updater` varchar(100) DEFAULT NULL,
  `email` varchar(200) DEFAULT NULL COMMENT '邮箱',
  `phone` varchar(20) DEFAULT NULL COMMENT '电话',
  `status` int(1) DEFAULT '1' COMMENT '雇员状态 在职 休假 离职',
  `branch_company` varchar(100) DEFAULT NULL COMMENT '隶属分公司',
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

