/*Table structure for table `faq` */

DROP TABLE IF EXISTS `faq`;

CREATE TABLE `faq` (
  `faq_id` int(11) NOT NULL AUTO_INCREMENT,
  `faq_desc` varchar(1000) DEFAULT NULL COMMENT '装修问题描述',
  `faq_answer` varchar(5000) DEFAULT NULL COMMENT '问题答案',
  `faq_type` varchar(100) DEFAULT NULL COMMENT '问题类型',
  `branch_company` varchar(100) DEFAULT NULL COMMENT '隶属分公司',
  `faq_status` int(1) DEFAULT '1' COMMENT '1正常 3删除',
  `create_time` datetime DEFAULT NULL,
  `creater` varchar(100) DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  `updater` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`faq_id`)
